import * as React from "react";
import { cva, type VariantProps } from "class-variance-authority";
import { cn } from "@/lib/utils";
import { Button as ShadcnButton, buttonVariants } from "@/components/ui/button";
import "@/components/ui/pixelact-ui/styles/styles.css";
import "./button.css";

const pixelButtonVariants = cva(
  "pixel__button pixel-font cursor-pointer rounded-none w-fit items-center justify-center whitespace-nowrap text-sm transition-colors transition-all duration-100",
  {
    variants: {
      variant: {
        default: "pixel-default__button box-shadow-margin",
        secondary: "pixel-secondary__button box-shadow-margin",
        warning: "pixel-warning__button box-shadow-margin",
        success: "pixel-success__button box-shadow-margin",
        destructive: "pixel-destructive__button box-shadow-margin",
        link: "pixel-link__button bg-transparent text-link underline-offset-4 underline",
      },
      size: {
        default: "h-10 px-4 py-2",
        sm: "h-9 px-3 text-xs",
        lg: "h-11 px-8 text-base",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "default",
    },
  }
);

export interface PixelButtonProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement>,
    VariantProps<typeof pixelButtonVariants> {
  asChild?: boolean;
}

const Button = React.forwardRef<
  React.ComponentRef<typeof ShadcnButton>,
  PixelButtonProps
>(({ className, variant, ...props }, ref) => {
  return (
    <ShadcnButton
      className={cn(
        buttonVariants({ variant }),
        pixelButtonVariants({ variant }),
        className
      )}
      ref={ref}
      {...props}
    />
  );
});

export { Button };

.pixel__button {
  position: relative;
  box-shadow: var(--pixel-box-shadow);

  &::after {
    content: "";
    position: absolute;
    bottom: 0;
    right: 0;
    width: 100%;
    height: 100%;
  }

  &:active {
    transform: translateY(3px);
  }
}

.pixel-default__button {
  background-color: white;
  color: black;
  &::after {
    border-right: 4px solid
      color-mix(in hsl, var(--default-inner-border-color) 70%, rgba(0, 0, 0));
    border-bottom: 4px solid
      color-mix(in hsl, var(--default-inner-border-color) 70%, rgba(0, 0, 0));
  }
  &:hover {
    background-color: var(--default-inner-border-color);
  }

  &:active {
    background-color: color-mix(
      in hsl,
      var(--default-inner-border-color) 70%,
      rgb(0, 0, 0)
    );
  }
}

.pixel-secondary__button {
  background-color: var(--color-secondary);
  color: var(--color-secondary-foreground);

  &::after {
    border-right: 4px solid
      color-mix(in hsl, var(--color-secondary) 70%, rgb(0, 0, 0));
    border-bottom: 4px solid
      color-mix(in hsl, var(--color-secondary) 70%, rgb(0, 0, 0));
  }

  &:hover {
    background-color: color-mix(
      in hsl,
      var(--color-secondary) 90%,
      rgb(0, 0, 0)
    );
  }

  &:active {
    background-color: color-mix(
      in hsl,
      var(--color-secondary) 70%,
      rgb(0, 0, 0)
    );
  }
}

.pixel-success__button {
  background-color: var(--success);
  color: var(--success-foreground);

  &::after {
    border-right: 4px solid color-mix(in hsl, var(--success) 70%, rgb(0, 0, 0));
    border-bottom: 4px solid color-mix(in hsl, var(--success) 70%, rgb(0, 0, 0));
  }

  &:hover {
    background-color: color-mix(in hsl, var(--success) 90%, rgb(0, 0, 0));
  }

  &:active {
    background-color: color-mix(in hsl, var(--success) 70%, rgb(0, 0, 0));
  }
}

.pixel-destructive__button {
  background-color: var(--destructive);
  color: var(--destructive-foreground);

  &::after {
    border-right: 4px solid
      color-mix(in hsl, var(--destructive) 70%, rgb(77, 77, 77));
    border-bottom: 4px solid
      color-mix(in hsl, var(--destructive) 70%, rgb(77, 77, 77));
  }

  &:hover {
    background-color: color-mix(
      in hsl,
      var(--destructive) 90%,
      rgb(77, 77, 77)
    );
  }

  &:active {
    background-color: color-mix(
      in hsl,
      var(--destructive) 70%,
      rgb(77, 77, 77)
    );
  }
}

.pixel-warning__button {
  background-color: var(--warning);
  color: var(--warning-foreground);

  &::after {
    border-right: 4px solid
      color-mix(in hsl, var(--warning) 70%, rgb(32, 32, 32));
    border-bottom: 4px solid
      color-mix(in hsl, var(--warning) 70%, rgb(32, 32, 32));
  }

  &:hover {
    background-color: color-mix(in hsl, var(--warning) 90%, rgb(32, 32, 32));
  }

  &:active {
    background-color: color-mix(in hsl, var(--warning) 70%, rgb(32, 32, 32));
  }
}

.pixel-link__button {
  box-shadow: none;
  color: var(--link);
  margin: 0;
  padding: 0;

  &:hover {
    background-color: transparent;
    color: color-mix(in hsl, var(--link) 70%, rgb(0, 0, 0));
  }

  &:active {
    color: color-mix(in hsl, var(--link) 90%, rgb(0, 0, 0));
  }

  &:visited {
    color: color-mix(in hsl, var(--link) 70%, rgb(0, 0, 0));
  }
}

@import url("https://fonts.googleapis.com/css2?family=Press+Start+2P&display=swap");

:root {
  --box-shadow-width: 4px;
  --pixel-box-shadow: calc(-1 * (var(--box-shadow-width))) 0 0 0 black,
    var(--box-shadow-width) 0 0 0 black, 0 var(--box-shadow-width) 0 0 black,
    0 calc(-1 * (var(--box-shadow-width))) 0 0 black;
  --pixel-box-shadow-destructive: calc(-1 * (var(--box-shadow-width))) 0 0 0
      var(--destructive),
    var(--box-shadow-width) 0 0 0 var(--destructive),
    0 var(--box-shadow-width) 0 0 var(--destructive),
    0 calc(-1 * (var(--box-shadow-width))) 0 0 var(--destructive);
  --default-inner-border-color: rgb(200, 200, 200);
}

.dark {
  --pixel-box-shadow: calc(-1 * (var(--box-shadow-width))) 0 0 0 var(--ring),
    var(--box-shadow-width) 0 0 0 var(--ring),
    0 var(--box-shadow-width) 0 0 var(--ring),
    0 calc(-1 * (var(--box-shadow-width))) 0 0 var(--ring);
}

.pixel-font {
  font-family: "Press Start 2P", system-ui, -apple-system, sans-serif;
  line-height: 1.5;
  letter-spacing: 0.5px;
}

.box-shadow-margin {
  margin: var(--box-shadow-width);
}
