"use client";

import * as React from "react";
import { cn } from "@/lib/utils";
import {
  Dialog as ShadcnDialog,
  DialogContent as ShadcnDialogContent,
  DialogTrigger,
  DialogPortal,
  DialogOverlay,
  DialogClose,
  DialogTitle,
  DialogDescription,
  DialogHeader,
  DialogFooter,
} from "@/components/ui/dialog";
import "@/components/ui/pixelact-ui/styles/styles.css";
import "./dialog.css";

export interface PixelDialogContentProps
  extends React.ComponentProps<typeof ShadcnDialogContent> {
  trigger?: React.ReactNode;
}

const Dialog = ({ ...props }: React.ComponentProps<typeof ShadcnDialog>) => {
  return <ShadcnDialog {...props} />;
};

const DialogContent = React.forwardRef<
  React.ComponentRef<typeof ShadcnDialogContent>,
  PixelDialogContentProps
>(({ className, ...props }, ref) => {
  return (
    <ShadcnDialogContent
      className={cn("pixel__dialog rounded-none", className)}
      ref={ref}
      {...props}
    />
  );
});

export {
  Dialog,
  DialogContent,
  DialogTrigger,
  DialogPortal,
  DialogOverlay,
  DialogClose,
  DialogTitle,
  DialogDescription,
  DialogHeader,
  DialogFooter,
};

.pixel__dialog {
  box-shadow: var(--pixel-box-shadow);
  border-radius: 0;
  background-color: var(--background);
  font-family: "Press Start 2P", system-ui, -apple-system, sans-serif;
  line-height: 1.5;
  letter-spacing: 0.5px;
}

.pixel__dialog[data-state="open"] {
  animation: none;
}

.pixel__dialog[data-state="closed"] {
  animation: none;
}

@import url("https://fonts.googleapis.com/css2?family=Press+Start+2P&display=swap");

.pixel-font {
  font-family: "Press Start 2P", system-ui, -apple-system, sans-serif;
  line-height: 1.5;
  letter-spacing: 0.5px;
}

.box-shadow-margin {
  margin: var(--box-shadow-width);
}
