"use client";

import * as React from "react";
import { cn } from "@/lib/utils";
import "@/components/ui/pixelact-ui/styles/styles.css";
import "./input.css";

export interface PixelInputProps
  extends React.InputHTMLAttributes<HTMLInputElement> {
  disabled?: boolean;
  className?: string;
}

const Input = React.forwardRef<HTMLInputElement, PixelInputProps>(
  ({ className, disabled, ...props }, ref) => {
    return (
      <input
        className={cn(
          "pixel__input pixel-font max-w-full outline-none p-2 bg-background text-foreground placeholder:text-sm md:placeholder:text-base box-shadow-margin disabled:opacity-40",
          disabled && "disabled:opacity-40 disabled:cursor-not-allowed",
          className
        )}
        disabled={disabled}
        ref={ref}
        {...props}
      />
    );
  }
);
Input.displayName = "PixelInput";

export { Input };

.pixel__input {
  box-shadow: var(--pixel-box-shadow);
  line-height: 1.5;
  letter-spacing: 0.5px;
}

.box-shadow-margin {
  margin: var(--box-shadow-width);
}

@import url("https://fonts.googleapis.com/css2?family=Press+Start+2P&display=swap");

.pixel-font {
  font-family: "Press Start 2P", system-ui, -apple-system, sans-serif;
  line-height: 1.5;
  letter-spacing: 0.5px;
}

.box-shadow-margin {
  margin: var(--box-shadow-width);
}
